"use strict";

function initCallingDialpad() {
  var n = 0;
  $(".digit").on("click", function () {
    var i = $(this).clone().children().remove().end().text();
    n < 10 && ($("#output").append("<span>" + i.trim() + "</span>"), n++);
  }), $(".clear").on("click", function () {
    $("#output span:last-child").remove(), n--;
  });
}
$(function () {
  initCallingDialpad();
});