"use strict";

function initAvatarUpload() {
  $(".file-upload").on("change", function () {
    var t;
    this.files && this.files[0] && ((t = new FileReader()).onload = function (t) {
      $(".upload-pic").attr("src", t.target.result);
    }, t.readAsDataURL(this.files[0]));
  }), $(".upload-button").on("click", function () {
    $(".file-upload").click();
  });
}
function initBirthDatePicker() {
  $("#birthDatePicker").flatpickr({
    altInput: !0,
    altFormat: "F j, Y",
    dateFormat: "Y-m-d"
  });
}
$(function () {
  initAvatarUpload(), initBirthDatePicker();
});