"use strict";

function initEditorToggler() {
  $('[data-cb-target="cb-email-editor-show"]').on("click", function () {
    $("#cb-email-editor").slideDown(), $("#cb-email-reply-btn").hide(), $("#cb-email-action-btn").show(), $("html,body").animate({
      scrollTop: $("#cb-email-editor").offset().top
    });
  }), $('[data-cb-target="cb-email-editor-hide"]').on("click", function () {
    $("#cb-email-editor").slideUp(), $("#cb-email-action-btn").hide(), $("#cb-email-reply-btn").show();
  });
}
function initCcBccToggler() {
  $("#cb-cc-toggle").on("click", function () {
    $('[data-cb-target="cb-email-cc"]').toggle();
  }), $("#cb-bcc-toggle").on("click", function () {
    $('[data-cb-target="cb-email-bcc"]').toggle();
  });
}
function initEmailEditor() {
  new Quill("#mailEditor", {
    modules: {
      toolbar: [["bold", "italic", "underline", "strike"], ["blockquote", "code-block"], [{
        size: ["small", !1, "large", "huge"]
      }], [{
        header: [1, 2, 3, 4, 5, 6, !1]
      }], [{
        header: 1
      }, {
        header: 2
      }], [{
        list: "ordered"
      }, {
        list: "bullet"
      }], [{
        script: "sub"
      }, {
        script: "super"
      }], [{
        indent: "-1"
      }, {
        indent: "+1"
      }], [{
        direction: "rtl"
      }], [{
        color: []
      }, {
        background: []
      }], [{
        font: []
      }], [{
        align: []
      }], ["clean"]]
    },
    theme: "snow",
    placeholder: "Compose an epic..."
  });
}
$(function () {
  initEditorToggler(), initCcBccToggler(), initEmailEditor();
});