"use strict";

function initCheckedAction() {
  $(document).on("change", "[data-checked-action='show-options']", function () {
    $(this).prop("checked") ? $(".cb-email-action-list").addClass("show-action") : $(".cb-email-action-list").removeClass("show-action");
  });
}
function initSelectChecked() {
  $("#checkAll").on("click", function () {
    this.checked ? $('.cb-email-action > [type="checkbox"]').each(function () {
      this.checked = !0;
    }) : $('.cb-email-action > [type="checkbox"]').each(function () {
      this.checked = !1;
    });
  }), $('.cb-email-action > [type="checkbox"]').on("click", function () {
    var c;
    $(this).is(":checked") ? (c = 0, $('.cb-email-action > [type="checkbox"]').each(function () {
      this.checked || (c = 1);
    }), 0 == c && $("#checkAll").prop("checked", !0)) : $("#checkAll").prop("checked", !1);
  });
}
$(function () {
  initCheckedAction(), initSelectChecked();
});